4.5 Functions

4.5.1 Temperature Determinants


Code
fn_t_determinants &lt;- function(df) {

  mcols &lt;- paste0("m", 1:12)
  df &lt;- df |&gt;
    mutate(
        tavg = (m1 + m2 + m3 + m4 + m5 + m6 + m7 + m8 + m9 + m10 + m11 + m12) / 12,
        tmax = pmax(m1, m2, m3, m4, m5, m6, m7, m8, m9, m10, m11, m12),
        tmin = pmin(m1, m2, m3, m4, m5, m6, m7, m8, m9, m10, m11, m12),
        tg10 = rowSums(across(all_of(mcols), ~ .x &gt;= 10))
    )  
  df &lt;- df |&gt;
    select(-m1, -m2, -m3, -m4, -m5, -m6, -m7, -m8, -m9, -m10, -m11, -m12)

  return(df)
}




4.5.2 Precipitation Determinants


Code
fn_p_determinants &lt;- function(df) {

  mcols &lt;- paste0("m", 1:12)
  df &lt;- df |&gt;
    mutate(
      ptot = (m1 + m2 + m3 + m4 + m5 + m6 + m7 + m8 + m9 + m10 + m11 + m12),
      pmin = pmin(m1, m2, m3, m4, m5, m6, m7, m8, m9, m10, m11, m12),
      pl60 = rowSums(across(all_of(mcols), ~ .x &lt; 60)),
      wtot = case_when(
        lat &gt;= 0 ~ (m1 + m2 + m3 + m10 + m11 + m12),
        lat &lt; 0 ~ (m4 + m5 + m6 + m7 + m8 + m9)
      ),
      stot = case_when(
        lat &gt;= 0 ~ (m4 + m5 + m6 + m7 + m8 + m9),
        lat &lt; 0 ~  (m1 + m2 + m3 + m10 + m11 + m12)
      )
    )     
  df &lt;- df |&gt;
    select(-m1, -m2, -m3, -m4, -m5, -m6, -m7, -m8, -m9, -m10, -m11, -m12)

  return(df)
}




4.5.3 Aridity Threshold


Code
fn_aridity_threshold &lt;- function(df) {

# Aridity threshold formula: R = 2.3T – 0.64Pw + 41 

    # R: mean annual precipitation threshold in cm

    # T: mean annual air temperature in °C
    # Pw: percentage of annual precipitation in winter (6 low-sun months)
    # winter: October to March in Northern Hemisphere
    #         April to September in Southern Hemisphere

  df &lt;- df |&gt;
    mutate(
      term1 = (2.3 * tavg),
      term2 = 
        if_else(ptot != 0, 
          (0.64 * ((wtot / ptot) * 100)), # multiply by 100 to convert to %
          0),
      term3 = 41,
      athr = (term1 - term2 + term3) * 10 # convert result from cm to mm

    )  |&gt;
    select(-term1, -term2, -term3)

  return(df)
}




4.5.4 Summarize Means


Code
fn_summarize_results &lt;- function(df) {
  df_means &lt;- df |&gt; 
    select(-c(1:4)) |&gt; 
    # this format is now recommended for dplyr
    summarise(across(everything(), \(x) mean(x, na.rm = TRUE))) |&gt; 
    pivot_longer(everything(),
               names_to = "stat_id",
               values_to = "stat_value")
  return(df_means)
}

5.8 Functions

5.8.1 Assign KTC Group


Code
fn_assign_grp &lt;- function(df) {
  df &lt;- df |&gt;
    mutate(
      grp = case_when(
        tmax &lt; 10 ~ "F",
        ptot &lt; athr ~ "B",
        tmin &gt;= 18 ~ "A",
        tg10 &gt;= 8 ~ "C",
        tg10 &gt;= 4 ~ "D",
        tg10 &gt;= 1 ~ "E",
        TRUE ~ "X"
      )
    )
  return(df)
}




5.8.2 Assign KTC Type


Code
fn_assign_typ &lt;- function(df) {
  df &lt;- df |&gt;
    mutate(
      cls = case_when(
        grp == "A" & (pl60 &lt;= 2) ~ "r",
        grp == "A" ~ "w",
        grp == "B" & (ptot &lt; athr/2) ~ "W",
        grp == "B" ~ "S",

        grp == "C" & (wtot &gt;= stot * 3) & (pmin &lt; 30) & (ptot &lt; 890) ~ "s",
        grp == "C" & (stot &gt;= wtot * 10) ~ "w",
        grp == "C" ~ "f",

        grp == "D" & (tmin &gt; 0) ~ "o",
        grp == "D" ~ "c",
        grp == "F" & (tmax &gt; 0) ~ "t",
        grp == "F" ~ "i",
        TRUE ~ ""
      ),
      typ = paste(grp, cls, sep = "")
    )
  df &lt;- df|&gt; select(lon, lat, area, lsm, grp, typ)
  return(df)
}




5.8.3 Create Map

5.8.3.1 Group


Code
fn_create_grp_map &lt;- function(plot_data, palette, cp_lbl) {

  p &lt;- ggplot(plot_data) +
    geom_sf(aes(color = grp), size = 0.01) +
    scale_color_manual(values = palette, drop = FALSE) +
    geom_sf(
      data = coastline_sf,
      color = "black",
      linewidth = 0.1
    ) +
    coord_sf(crs = rbn_prj) +
    labs(
      title = "Köppen-Trewartha Climate Classification Groups",
      subtitle = cp_lbl,
      color = "Group"
    ) +
    guides(
      color = guide_legend(
        override.aes = list(
          size = 5 # legend point size only
        )
      )
    )
    return(p)
}




