5.9 Main Process


Code
# Step 1 assign

crp_grp_df &lt;- fn_assign_grp(crp_determinants_df)
crp_typ_df &lt;- fn_assign_typ(crp_grp_df)

rcp_grp_df &lt;- fn_assign_grp(rcp_determinants_df)
rcp_typ_df &lt;- fn_assign_typ(rcp_grp_df)

# Step 2 select

crp_grp_df &lt;- crp_grp_df |&gt; select(lat,lon,area,lsm,grp)
crp_typ_df &lt;- crp_typ_df |&gt; select(lat,lon,area,lsm,typ)

rcp_grp_df &lt;- rcp_grp_df |&gt; select(lat,lon,area,lsm,grp)
rcp_typ_df &lt;- rcp_typ_df |&gt; select(lat,lon,area,lsm,typ)

# Step 3 join

ktc_grp_df &lt;- crp_grp_df %&gt;% 
  left_join(rcp_grp_df , by = c("lon", "lat", "lsm")) |&gt;
  rename(cgrp = grp.x, rgrp = grp.y, area = area.x) |&gt;
  select(lon, lat, lsm, area, cgrp, rgrp) |&gt;
  mutate(cgrp = factor(cgrp, levels = ktc_grp_palette_df$grp)) |&gt;
  mutate(rgrp = factor(rgrp, levels = ktc_grp_palette_df$grp))

ktc_typ_df &lt;- crp_typ_df %&gt;% 
  left_join(rcp_typ_df , by = c("lon", "lat", "lsm")) |&gt;
  rename(ctyp = typ.x, rtyp = typ.y, area = area.x) |&gt;
  select(lon, lat, lsm, area, ctyp, rtyp) |&gt;
  mutate(ctyp = factor(ctyp, levels = ktc_typ_palette_df$typ)) |&gt;
  mutate(rtyp = factor(rtyp, levels = ktc_typ_palette_df$typ))

# Step 4 convert to sf

ktc_grp_sf &lt;- st_as_sf(ktc_grp_df, coords = c("lon", "lat"), crs = 4326)
ktc_typ_sf &lt;- st_as_sf(ktc_typ_df, coords = c("lon", "lat"), crs = 4326)

# Step 5 summarize

ktc_grp_cmb_sf &lt;- ktc_grp_sf |&gt;
  filter(lsm &gt; 0) |&gt;
  summarise(
    geometry = st_combine(geometry),
    mkm = sum(area*lsm),
    .by = c(cgrp, rgrp)
  ) |&gt;
  mutate(pct = round(mkm / sum(mkm) * 100, 3)) |&gt;
  select(cgrp, rgrp, mkm, pct, geometry) |&gt;
  arrange(cgrp, rgrp) |&gt;
  st_set_crs(4326)

ktc_typ_cmb_sf &lt;- ktc_typ_sf |&gt;
  filter(lsm &gt; 0) |&gt;
  summarise(
    geometry = st_combine(geometry),
    mkm = sum(area*lsm),
    .by = c(ctyp, rtyp)
  ) |&gt;
  mutate(pct = round(mkm / sum(mkm) * 100, 3)) |&gt;
  select(ctyp, rtyp, mkm, pct, geometry) |&gt;
  arrange(ctyp, rtyp) |&gt;
  st_set_crs(4326)

# Step 6 crp summary df

crp_grp_tbl_df &lt;- ktc_grp_cmb_sf |&gt;
  st_drop_geometry() |&gt;
  rename(grp = cgrp) |&gt;
  select(grp, mkm, pct) |&gt;
  summarise(
    mkm = sum(mkm),
    pct = sum(pct),
    .by = grp
  )|&gt;
  arrange(grp)

crp_typ_tbl_df &lt;- ktc_typ_cmb_sf |&gt;
  st_drop_geometry() |&gt;
  rename(typ = ctyp) |&gt;
  select(typ, mkm, pct) |&gt;
  summarise(
    mkm = sum(mkm),
    pct = sum(pct),
    .by = typ
  )|&gt;
  arrange(typ)

# Step 7 rcp summary df

rcp_grp_tbl_df &lt;- ktc_grp_cmb_sf |&gt;
  st_drop_geometry() |&gt;
  rename(grp = rgrp) |&gt;
  select(grp, mkm, pct) |&gt;
  summarise(
    mkm = sum(mkm),
    pct = sum(pct),
    .by = grp
  )|&gt;
  arrange(grp)

rcp_typ_tbl_df &lt;- ktc_typ_cmb_sf |&gt;
  st_drop_geometry() |&gt;
  rename(typ = rtyp) |&gt;
  select(typ, mkm, pct) |&gt;
  summarise(
    mkm = sum(mkm),
    pct = sum(pct),
    .by = typ
  )|&gt;
  arrange(typ)

6.8 Functions

6.8.1 Calculate Group Changes


Code
fn_climate_group_changes &lt;- function(sf) {
  crp_grp_sf &lt;- sf |&gt;
    select(cgrp, mkm, pct, geom) |&gt;
    summarize(
      mkm = sum(mkm),
      pct = sum(pct),
      geom = st_combine(geom),
      .by = c(cgrp)) |&gt;
      rename(cg = cgrp)

  rcp_grp_sf &lt;- sf |&gt;
    select(rgrp, mkm, pct, geom) |&gt;
    summarize(
      mkm = sum(mkm),
      pct = sum(pct),
      geom = st_combine(geom),
      .by = c(rgrp))|&gt;
      rename(cg = rgrp)

  crp_grp_df &lt;- crp_grp_sf |&gt; st_drop_geometry()
  rcp_grp_df &lt;- rcp_grp_sf |&gt; st_drop_geometry()

  join_df &lt;- crp_grp_df  |&gt; 
    inner_join(rcp_grp_df, by = "cg") |&gt;
    mutate(
      mkm.chg = mkm.y - mkm.x,
      pct.chg = pct.y - pct.x
    ) |&gt;
    select(cg, mkm.x, mkm.y, mkm.chg, pct.x, pct.y, pct.chg)

  return(join_df)
}




6.8.2 Build Group Changes Summary Table


Code
fn_build_summary_grp_table &lt;- function(df) {
  tbl &lt;- df |&gt;
    gt(rowname_col = "cg" ) |&gt;
    tab_stubhead(label = "Group")|&gt;
    tab_header(
      title = "Köppen-Trewartha Climate Group Changes",
    ) |&gt;
    tab_spanner(
      label = "Area (Mkm²)",
      columns = c(mkm.x, mkm.y, mkm.chg)
    ) |&gt;
    tab_spanner(
      label = "Group % of Total",
      columns = c(pct.x, pct.y, pct.chg)
    ) |&gt;
    cols_label(
      cg = "Climate Group",
      mkm.x = "CRP",
      mkm.y = "RCP",
      mkm.chg = "Chg",
      pct.x = "CRP",
      pct.y = "RCP",
      pct.chg = "Chg"
    ) |&gt;
    fmt_number(columns = c(mkm.x, mkm.y, mkm.chg), decimals = 1) |&gt;
    fmt_number(columns = c(pct.x, pct.y, pct.chg), decimals = 2) |&gt;
    grand_summary_rows(
      columns = c(mkm.x, mkm.y, mkm.chg, pct.x, pct.y, pct.chg),
      fns = list("Total" = ~ sum(.)),
      fmt = list(~ fmt_number(.x, decimals = 2))
    ) |&gt;
    tab_footnote(
      footnote = paste("Climatological Reference Period ", crp_beg, "-", crp_end, sep = ""),
      locations = cells_column_labels(columns = c(mkm.x, pct.x))
    ) |&gt;
    tab_footnote(
      footnote = paste("Recent Climate Period ", rcp_beg, "-", rcp_end, sep = ""),
      locations = cells_column_labels(columns = c(mkm.y, pct.y))
    ) |&gt;
    tab_style(
      style = cell_fill(color = "lightblue"),
      locations = cells_body(
        rows = abs(pct.chg) &gt; 1   # condition here
      )
    )
}




6.8.3 Create Group Changes Map


Code
fn_create_grp_map &lt;- function(plot_data, palette, cp) {

  p &lt;- ggplot(plot_data) +
    geom_sf(aes(color = grp), size = 0.01) +
    scale_color_manual(values = palette, drop = FALSE) +
    geom_sf(data = coastline_sf, color = "black", linewidth = 0.1) +
    coord_sf(crs = rbn_prj) +
    guides(color = guide_legend(override.aes = list(size = 4 ))) # legend point size

  if (cp == "rcp") {
    p &lt;- p +  
      labs(
        title = "Group Classification Changes ",
        color = "New\nGroup"
      )
  } else {
    p &lt;- p + labs(color = "Old\nGroup")
  }

  return(p)
}

